import React, { useState, useEffect } from 'react';
import { withRouter } from 'react-router-dom';
import './Dashboard.css';

const Dashboard = ({ location }) => {
  const [data, setData] = useState([]);
  const [ipAddress, setIPAddress] = useState('');

  useEffect(() => {
    fetch('https://geolocation-db.com/json/')
      .then(response => response.json())
      .then(data => {
        setIPAddress(data.IPv4);
      })
      .catch(error => console.log(error));
  }, []);

  useEffect(() => {
    console.log("useEffect triggered");

    const searchParams = new URLSearchParams(location.search);
    const pid = searchParams.get('pid');
    const uid = searchParams.get('uid');

    // Refactored logic to create newData and send to backend within useEffect
    fetch('https://geolocation-db.com/json/')
      .then(response => response.json())
      .then(data => {
        setIPAddress(data.IPv4);

        if (pid && uid && ipAddress) {
          const completionTime = new Date().toISOString().slice(0, 10);
          const statusValue = window.location.pathname.includes('terminate') ? 'Terminate' : (window.location.pathname.includes('quotafull') ? 'Quotafull' : 'Completed');

          const newData = {
            pid,
            uid,
            ip: ipAddress,
            statusValue,
            completionTime,
          };

          console.log('New Data:', newData);
          setData(prevData => [...prevData, newData]);

          // Send data to backend
          fetch('http://localhost:8083/survey', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json'
            },
            body: JSON.stringify(newData)
          })
          .then(response => {
            if (response.ok) {
              console.log('Data saved successfully');
            } else {
              console.error('Failed to save data');
            }
          })
          .catch(error => {
            console.error('Error saving data:', error);
          });
        }
      })
      .catch(error => console.error(error));
  }, [location.search]); // No need for ipAddress in dependency array anymore

  return (
    <div className="container-fluid pt-5">
      <div className="row">
        <main className="col-md-9 ms-sm-auto col-lg-10 px-md-4">
          <div className="d-flex justify-content-between flex-wrap flex-md-nowrap align-items-center pt-3 pb-2 mb-3 border-bottom">
            <h1 className="h2">Dashboard</h1>
            <div className="btn-toolbar mb-2 mb-md-0"></div>
          </div>

          <form className="form-horizontal" method="POST">
            <div className="form-group">
              <input type="text" name="uni" className="form-control" placeholder="Universal" />
            </div>
            <div className="form-group">
              <input type="text" name="p_id" className="form-control" placeholder="PID" />
            </div>
            <div className="form-group">
              <input type="text" name="u_ide" className="form-control" placeholder="UID Ends-with" />
            </div>
            <div className="form-group">
              <input type="text" name="u_ids" className="form-control" placeholder="UID starts-with" />
            </div>
            <div className="form-group">
              <input type="date" name="date" className="form-control" placeholder="Date" />
            </div>
            <div className="form-group">
              <select className="form-select" name="status">
                <option style={{ display: 'none' }} selected value="">Status...</option>
                <option value="complete">Complete</option>
                <option value="terminate">Terminate</option>
                <option value="quotafull">Quotafull</option>
              </select>
            </div>
            <div className="form-group">
              <button type="submit" className="btn btn-primary">Filter</button>
            </div>
          </form>

          <div className="table-container">
            <div className="table-responsive">
              <table className="table table-bordered table-hover table-sm text-center">
                <thead>
                  <tr>
                    <th scope="col">Serial NO.</th>
                    <th scope="col">Project ID</th>
                    <th scope="col">User ID</th>
                    <th scope="col">Status</th>
                    <th scope="col">IP Address</th>
                    <th scope="col">Completion Time</th>
                  </tr>
                </thead>
                <tbody>
                  {data.length > 0 ? (
                    data.map((item, index) => (
                      <tr key={index}>
                        <td>{index+1}</td>
                        <td>{item.pid}</td>
                        <td>{item.uid}</td>
                        <td>{item.statusValue}</td>
                        <td>{item.ip}</td>
                        <td>{item.completionTime}</td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="6">No data available</td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          </div>

          <span>Total ({data.length})</span>

          <button onClick={() => {}} className="btn btn-export">
            Export
          </button>

          <nav aria-label="...">
            <ul className="pagination justify-content-center"></ul>
          </nav>
        </main>
      </div>
    </div>
  );
};

export default withRouter(Dashboard);
